var icons=document.getElementById("icons");for(var i=0;i<localStorage.length;i++){var key=localStorage.key(i);if(key.substr(0,2)=="i:"){var value=localStorage.getItem(key);icons.innerHTML+='<img src="'+value+'" title="'+key.substr(2)+" ("+Math.round(value.length/1024)+')">'}}var notifications=document.getElementById("notifications");var n=localStorage.getItem("list");if(n!=null){notifications.textContent=JSON.stringify(JSON.parse(n),null,2)}document.getElementById("clear").onclick=function(){localStorage.clear();window.location.reload()};