$(document).ready(function(){if(window.location.hash=="#iframe"){$("h3").hide()}$("#locale-title").text(chrome.i18n.getMessage("devices_connected_devices"));$("#locale-header").text(chrome.i18n.getMessage("devices_connected_devices"));$("#locale-loading").text(chrome.i18n.getMessage("devices_loading"));var list=$("#list");chrome.storage.local.get("device",function(items){var thisDevice=items.device;chrome.identity.getAuthToken({interactive:false},function(token){if(token){getInstanceID(function(iid){$.post(URL_DEVICES,{data:JSON.stringify({token:token,iid:iid})},function(data){list.html("");for(var d in data){if(data.hasOwnProperty(d)){var item=$("<li>").attr("class","list-group-item").attr("title",chrome.i18n.getMessage("devices_time_added")+new Date(data[d].time).toLocaleString()).text(" "+data[d].name);if(data[d].name==thisDevice){item.attr("style","font-weight: bold");item.text(item.text()+" ("+chrome.i18n.getMessage("devices_this_device")+")")}list.append(item);var img=$("<img>").attr("src","images/ic_help_outline_black_24px.svg");switch(data[d].type){case TYPE_ANDROID_PHONE:img.attr("src","images/ic_phone_android_black_24px.svg");break;case TYPE_ANDROID_TABLET:img.attr("src","images/ic_tablet_black_24px.svg");break;case TYPE_CHROME:case TYPE_FIREFOX:case TYPE_OPERA:case TYPE_VIVALDI:case TYPE_EDGE:case TYPE_SAFARI:img.attr("src","images/ic_computer_black_24px.svg");break;case TYPE_ANDROID_TV:case TYPE_FIRE_TV:img.attr("src","images/ic_tv_black_24px.svg");break}item.prepend(img)}}}).fail(function(){list.html('<li class="list-group-item"><em>'+chrome.i18n.getMessage("devices_error")+"</em></li>")})})}})})});